import { useState } from 'react'

type Ingredient = {
  name: string
  type: 'staple' | 'perishable' | 'must-use'
}

type Recipe = {
  title: string
  image: string
}

export default function FridgeChef() {
  const [ingredients, setIngredients] = useState<Ingredient[]>([])
  const [recipes, setRecipes] = useState<Recipe[]>([])

  const addIngredient = (type: Ingredient['type']) => {
    const name = prompt(`Enter ${type} ingredient:`)
    if (name) {
      setIngredients([...ingredients, { name, type }])
    }
  }

  const generateRecipes = () => {
    // This is where you'd typically call an API to generate recipes
    // For this MVP, we'll just set some dummy data
    setRecipes([
      {
        title: '千層麵',
        image:
          'data:image/jpeg;base64,'
      },
      {
        title: '比薩',
        image:
          'data:image/jpeg;base64,'
      },
      {
        title: '義大利麵',
        image:
          'data:image/jpeg;base64,'
      }
    ])
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Fridge Chef</h1>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="space-y-4">
          {['staple', 'perishable', 'must-use'].map((type) => (
            <div key={type} className="bg-white shadow rounded-lg p-4">
              <h2 className="text-lg font-semibold mb-2 capitalize">{type}</h2>
              <button
                onClick={() => addIngredient(type as Ingredient['type'])}
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
              >
                Add {type}
              </button>
            </div>
          ))}
          <button
            onClick={generateRecipes}
            className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
          >
            Generate
          </button>
        </div>
        <div className="col-span-3 grid grid-cols-1 md:grid-cols-3 gap-4">
          {recipes.map((recipe, index) => (
            <div
              key={index}
              className="bg-white shadow rounded-lg overflow-hidden"
            >
              <h3 className="text-lg font-semibold p-4">{recipe.title}</h3>
              <img
                src={recipe.image}
                alt={recipe.title}
                className="w-full h-auto"
              />
            </div>
          ))}
        </div>
      </div>
      <div className="mt-4">
        <h2 className="text-xl font-semibold mb-2">Ingredients List</h2>
        <ul className="list-disc pl-5">
          {ingredients.map((ingredient, index) => (
            <li key={index}>
              {ingredient.name} -{' '}
              <span className="capitalize">{ingredient.type}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  )
}
